import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import Link from 'next/link';

const Person = props => (
    <Layout>
        <div className="personIntro">
            <div className="personInfo">
                <h1>{props.personData.name}</h1>
                <div>{props.personData.deathday ? <h3>Born: {props.personData.birthday}, {props.personData.country.name} <br /> Dead: {props.personData.deathday}</h3> : <h3>Born: {props.personData.birthday}, {props.personData.country.name}</h3>}</div>
            </div>
            <div>{props.personData.image ? <img src={props.personData.image.original} /> : <noimg />}</div>

        </div>
        <ul className="showsList">
            {props.actorData.map(show => (
                <li key={show.id}>

                    <h3>{show._embedded.show.name} ({show._embedded.show.network ? show._embedded.show.network.name : show._embedded.show.webChannel.name})</h3>
                    <h4>{show._embedded.show.genres.join(" / ")}</h4>
                    <div>{show._embedded.show.image ? <img src={show._embedded.show.image.original} /> : <noimg />}</div>
                    <div dangerouslySetInnerHTML={props.summary} />

                </li>
            ))}
        </ul>
        <style jsx>{`
        .showsList img {
            height: 50vh;
        }
        .personIntro {
            display: flex;
            flex-direction: row;
        }
        .personInfo {
            width: 100vw;
        }
       .personIntro img{
           height: 50vh;
           display: flex;
       }
      `}</style>
    </Layout>
);

Person.getInitialProps = async function (context) {
    const { id } = context.query;

    const [personData, actorData] = await Promise.all([
        fetch(`https://api.tvmaze.com/people/${id}`).then(r => r.json()),
        fetch(`https://api.tvmaze.com/people/${id}/castcredits?embed=show`).then(r => r.json()),

    ])

    return {
        personData, actorData, summary: { __html: data._embedded.show.summary }

    };
};

export default Person;
