import Link from 'next/link';
import Layout from '../components/MyLayout';
import fetch from 'isomorphic-unfetch';


const Blog = props => (
    <Layout>
        <div className="container">

            <div className="summary">
                <h1>{props.data.name}'s unofficial Blog</h1>
                <p>{props.data.summary.replace(/<[^>]*>?/gm, '')}</p>
                <ul className="list">
                    <li>
                        <Link href="/seasons/${props.data.id}" as={`/seasons/${props.data.id}`}>
                            <a>Episodes</a>
                        </Link>
                    </li>
                    <li>
                        <Link href="/${props.data.id}" as={`/cast/${props.data.id}`}>
                            <a>Cast</a>
                        </Link></li>
                    <li>
                        <Link href="/crew">
                            <a>Crew</a>
                        </Link></li>
                </ul>
            </div>
            <div className="img">{props.data.image ? <img src={props.data.image.original} /> : <noimg />}</div>
        </div>
        <style>{`
            .container {
                width: 70%;
                display: flex;

            }
            .summary {
                width: 30vw;

            }
            .img {
                max-width: 20vw;
                float: right;

            }
       `}
        </style>
    </Layout>
)

Blog.getInitialProps = async function (context) {
    const { id } = context.query;

    const res = await fetch(`https://api.tvmaze.com/singlesearch/shows?q=${id}`);
    const data = await res.json();
    console.log(id);
    return {
        data
    };
};

export default Blog
