import Layout from '../components/MyLayout';

export default function About() {
    return (
        <Layout>
            <div className="container">
                <h1>About this unofficial blog:</h1>
                <p>This app was created by Andrés Martín, an Uruguayan self-taught developer.</p>
                <p>It was created using React.js and its framework Next.js.
                All its pages are dynamic, meaning that their content is being created on the spot, based on the client's input.</p>
                <p>That's why this app can be any show's unofficial blog! It fetches the information on the spot from TV MAZE's API.</p>
                <br />
                <p>We hope you enjoy it!</p>
                <br />
                <p>XOXO</p>
            </div>
            <style jsx>{`
                .container {
                    padding: 10%;
                }
                `}
            </style>
        </Layout>
    );
}
