import Link from 'next/link';
import Layout from '../../components/MyLayout';

const Cast = props => (

    <Layout>
        <h1>Cast</h1>
        <ul>
            {props.cast.map(person => (
                <li key={person.person.id} >
                    <Link href="/cast/[id]" as={`/cast/${person.person.id}`}>
                        <a>{person.person.name}........{person.character.name}</a>
                    </Link>
                </li>
            ))}
        </ul>

    </Layout>
);

Cast.getInitialProps = async function () {
    const res = await fetch('http://api.tvmaze.com/shows/385/cast');
    const data = await res.json();

    return {
        cast: data.map(entry => entry)
    };
};

export default Cast;
