import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import Link from 'next/link';

const Person = props => (
    <Layout>
        <div className="personIntro">
            <div className="personInfo">
                <h1>{props.personData.name}</h1>
            </div>
            <div>{props.personData.image ? <img src={props.personData.image.original} /> : <noimg />}</div>

        </div>
        <ul className="showsList">
            {props.crewData.map(show => (
                <li key={show.id}>

                    <h3>{show.type} <br></br>{show._embedded.show.name} ({show._embedded.show.network ? show._embedded.show.network.name : show._embedded.show.webChannel.name})</h3>
                    <h4>{show._embedded.show.genres.join(" / ")}</h4>
                    <div>{show._embedded.show.image ? <img src={show._embedded.show.image.original} /> : <noimg />}</div>
                    <p>{show._embedded.show.summary.substring(3, show._embedded.show.summary.length - 4).replace(/<[^>]*>?/gm, '')}</p>

                </li>
            ))}
        </ul>
        <style jsx>{`
        .showsList img {
            height: 50vh;
        }
        .personIntro {
            display: flex;
            flex-direction: row;
        }
        .personInfo {
            width: 100vw;
        }
       .personIntro img{
           height: 50vh;
           display: flex;
       }
      `}</style>
    </Layout>
);

Person.getInitialProps = async function (context) {
    const { id } = context.query;

    const [personData, crewData] = await Promise.all([
        fetch(`https://api.tvmaze.com/people/${id}`).then(r => r.json()),
        fetch(`https://api.tvmaze.com/people/${id}/crewcredits?embed=show`).then(r => r.json()),

    ])

    return {
        personData, crewData,

    };
};

export default Person;
