import { useForm, useState } from "react-hook-form";

const Choose = () => {

    const [show, setShow] = useState("")

    const { register, handleSubmit, errors } = useForm();

    const onSubmit = (data) => {
        console.log(data)
        setShow(data.name)

    }



    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)} >
                <label>
                    Pick a show:
                </label>
                <input type="text" name="name" placeholder="Mad Men, The Sopranos, The Wire..." ref={register({ required: true })} />
                {errors.name && <p>Write something!</p>}
                <input type="submit" value="Submit" />
            </form>
        </div>
    )
}

export default Choose
