import Link from 'next/link';
import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import { useRouter } from 'next/router'

const Episodes = props => {

    const router = useRouter()
    return (
        <Layout>
            <h1>{props.name}</h1>
            <ul>
                {props.seasons.map(season => (
                    <li key={season.id} >
                        <div>
                            {season.premiereDate ? <Link href="/season/[id]" as={`/season/${season.id}`}>
                                <a>Season {season.number} - {season.premiereDate} / {season.endDate} </a>

                            </Link>
                                :
                                <p>Season {season.number}</p>
                            }
                        </div>

                    </li>
                ))}
            </ul>

        </Layout>
    )
};

Episodes.getInitialProps = async function (context) {
    const { id } = context.query
    const [name, seasons] = await Promise.all([
        fetch(`https://api.tvmaze.com/shows/${id}`).then(r => r.json().name),
        fetch(`https://api.tvmaze.com/shows/${id}/seasons`).then(r => r.json().map.then((entry => entry))),
    ])
    return {
        name, seasons

    };
};

export default Episodes
