import { useForm } from "react-hook-form";
import { useState } from "react";
import Link from 'next/link';
<link href="https://fonts.googleapis.com/css2?family=Roboto+Mono:wght@300&display=swap" rel="stylesheet"></link>

const Choose = () => {

    const [show, setShow] = useState("")
    const { register, handleSubmit, errors } = useForm();

    const onSubmit = async function (data) {
        if (await fetch(`https://api.tvmaze.com/singlesearch/shows?q=${data}`) == true)
            await setShow(data.name)


    }



    const PostLink = props => (

        <Link href={"/[id]"} as={`/${props.id}`}>
            <a>Let's go!</a>
        </Link>

    );

    return (
        <div className="indexContainer">
            <form className="indexForm" onSubmit={handleSubmit(onSubmit)} >
                <label>
                    Pick a show:
                </label>
                <input
                    className="input"
                    type="text" name="name"
                    placeholder="Mad Men, The Wire..."
                    ref={register({ required: true, validate: async value => await fetch(`https://api.tvmaze.com/singlesearch/shows?q=${value}`) !== undefined })}
                />
                {errors.name && <p>Not a show</p>}
                <input className="button" type="submit" value="I'm ready" />
                <div key={show}>
                    {show ? <div><div className="link"><PostLink id={show} /></div> <p className="quote">"Dj Khaled, 2018"</p></div> : <p>Please!</p>}
                </div>
            </form>
            <style jsx>{`
                .indexContainer {
                    position: absolute; top: 0; left: 0;
                    width: 100%; 
                    height: 100%;
                    background-color: #33DEA0;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    font-family: 'Roboto Mono', monospace;
                }
                .indexForm {
                    background-color: #33C7DE;
                    margin-bottom: 6vh;
                    padding: 10px;
                    display: flex;
                    justify-content: center;
                    align-items: center; 
                    flex-direction: column;
                    jusfity-content: space-between;
                }
                .indexForm > * {
                    padding: 10px;
                }

                .button {
                    display:inline-block;

                     padding:0.35em 1.2em;
                     border:0.1em solid #FFFFFF;
                     margin:1em 0.3em 0.3em 0;
                     border-radius:0.12em;
                     box-sizing: border-box;
                     text-decoration:none;
                    background-color: transparent;
                     font-weight:300;
                     color:#FFFFFF;
                     text-align:center;
                     transition: all 0.2s;
                }
                .button:hover{
                     color:#000000;
                     background-color:#FFFFFF;
                }


                .quote {
                    font-size: 9px;
                }

            `}
            </style>

        </div>
    )
}

export default Choose
