import Link from 'next/link';
import Layout from '../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Blog = props => (
    <Layout>
        <h1>The Mad Men unofficial Blog</h1>
        <p>{props.data.summary.replace(/<[^>]*>?/gm, '')}</p>

        <ul>
            <li>
                <Link href="/seasons">
                    <a>Episodes</a>
                </Link>
            </li>
            <li>
                <Link href="/cast">
                    <a>Cast</a>
                </Link></li>
            <li>
                <Link href="/crew">
                    <a>Crew</a>
                </Link></li>
        </ul>
        <div>{props.data.image ? <img src={props.data.image.original} /> : <noimg />}</div>
    </Layout>
);

Blog.getInitialProps = async function (context) {
    const { show } = context.query;
    const res = await fetch('http://api.tvmaze.com/shows/${show}');
    const data = await res.json();

    console.log(data.summary)

    return {
        data
    };
};

export default Blog
