import Link from 'next/link';
import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Episodes = props => (
    <Layout>
        <div className="container">
            <h1 className="title">{props.title}'s Unofficial Blog</h1>
            <ul className="list">
                <h2>All seasons!</h2>
                {props.seasons.map(season => (
                    <li key={season.id} >
                        <div>
                            {season.premiereDate ? <Link href="/season/[id]" as={`/season/${season.id}`}>
                                <a>Season {season.number} - {season.premiereDate} / {season.endDate} </a>

                            </Link>
                                :
                                <p>Season {season.number}</p>
                            }
                        </div>

                    </li>
                ))}
            </ul>
        </div>
        <style jsx>{`
        .title {
            grid-column-start: 1;
            grid-column-end: 5;
            grid-row-start: 1;
            grid-row-end: 2;
            display: flex;
            align-items: center;
            justify-content: center;
            margin-bottom: 0px;
        }
        .list {
            grid-column-start: 1;
            grid-column-end: 4;
            grid-row-start: 2;
            grid-row-end: 5;
            margin: 0;
                padding: 0;
                align-items: center;
                justify-content: center;
                text-align: center

        }
        li {
            list-style-type: none;
                height: 13vh;
                display: flex;
                align-items: center;
                justify-content: center;
        }
        .container {
            padding-top: 15px;
            justify-content: stretch;   
            align-content: stretch;     
            display: grid;
            grid-template-columns: 20vw 20vw 20vw 30vw ;
            grid-template-rows: 10vh 45vh 20vh 19vh;
            margin-left: 6%; 
            margin-right: 6%;      
         }
        `}

        </style>

    </Layout>
);

Episodes.getInitialProps = async function (context) {
    const { id } = context.query
    const [show, seasons] = await Promise.all([
        fetch(`https://api.tvmaze.com/shows/${id}`).then(r => r.json()),
        fetch(`https://api.tvmaze.com/shows/${id}/seasons`).then(r => r.json()),
    ])

    return {
        title: show.name,
        seasons: seasons.map(entry => entry)
    };
};

export default Episodes
