import Link from 'next/link';
import Layout from '../components/MyLayout';

function Cast() {
    return (
        <Layout>
            <h1>Cast</h1>
            <ul>
                {props.cast.map(person => (
                    <li key={person.id} >
                        <Link href="/seasons/[id]" as={`/seasons/${person.id}`}>
                            <a>{person.name}........{person.character}</a>
                        </Link>
                    </li>
                ))}
            </ul>

        </Layout>
    );
}

Cast.getInitialProps = async function () {
    const res = await fetch('http://api.tvmaze.com/shows/385/cast');
    const data = await res.json();

    return {
        cast: data.map(entry => entry)
    };
};

export default Cast;
