import { useForm } from "react-hook-form";
import { useState } from "react";
import Link from 'next/link';

const Choose = () => {

    const [show, setShow] = useState("")

    const { register, handleSubmit } = useForm();

    const onSubmit = async function (data) {
        console.log(data.name)
        await setShow(data.name)
        await console.log(show)
    }

    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)} >
                <label>
                    Pick a show:
                </label>
                <input type="text" name="name" placeholder="Mad Men, The Sopranos, The Wire..." ref={register({ required: true })} />

                <input type="submit" value="Submit" />
            </form>
            <div key={name}>

                {show ? <Link href="/[name]"><a>Now we're ready</a></Link> && console.log(name) : <p>Please!</p>}
            </div>
        </div>
    )
}

export default Choose
