import Layout from '../../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Episode = props => (

    <Layout>
        <div className="container">
            <h1 className="title">Season {props.episode.season} - Episode {props.episode.number}</h1>
            <div className="list">
                <h2>{props.episode.name}</h2>

                <div>{props.summary ? <div>
                    <p>{props.summary.replace(/<[^>]*>?/gm, '')}</p>

                </div> : <noimg />}</div>

                <p>Airdate: {props.episode.airdate}</p>
            </div>
            <div className="img">{props.episode.image ? <img src={props.episode.image.original} /> : <noimg />}</div>
        </div>
        <style jsx>{`
        .title {
            grid-column-start: 1;
            grid-column-end: 5;
            grid-row-start: 1;
            grid-row-end: 2;
            display: flex;
            align-items: center;
            justify-content: center;
            margin-bottom: 0px;
        }
        .list {
            grid-column-start: 1;
            grid-column-end: 3;
            grid-row-start: 2;
            grid-row-end: 5;
            margin: 0;
                padding: 0;
                align-items: center;
                justify-content: center;
                text-align: center

        }
        li {
            list-style-type: none;
                height: 13vh;
                display: flex;
                align-items: center;
                justify-content: center;
        }
        .img {
            grid-column-start: 3;
                grid-column-end: 5;
                grid-row-start: 2;
                grid-row-end: 5;
                justify-self: center;
                margin-top: 20px;
                display: flex;
         justify-content: center;
         align-items: center;
        }
        .img > img {
            object-fit: cover;
           max-height: 100%;  
           max-width: 100%;       
    }
        .container {
            padding-top: 25px;
            justify-content: stretch;   
            align-content: stretch;     
            display: grid;
            grid-template-columns: 20vw 20vw 20vw 20vw ;
            grid-template-rows: 10vh 40vh 20vh 19vh;
            margin-left: 6%; 
            margin-right: 6%;      
         }
         @media (max-width: 800px){
            Layout {
                font-size: 10px;
            }
            .container {
                padding-top: 15px;
                justify-content: stretch;   
                align-content: stretch;     
                display: grid;
                grid-template-columns: 20vw 20vw 20vw 20vw ;
                grid-template-rows: 15vh 15vh 20vh 19vh;
                margin-left: 9%; 
                margin-right: 9%;
                font-size: 9px;     
             }
             .img {
                grid-column-start: 1;
                grid-column-end: 5;
                grid-row-start: 1;
                grid-row-end: 3;
                justify-self: center;
                padding-top: 2vh
            }
            .summary {
                grid-column-start: 1;
                grid-column-end: 5;
                grid-row-start: 3;
                grid-row-end: 4;
            }
            .list {
                grid-column-start: 1;
                grid-column-end: 5;
                grid-row-start: 3;
                grid-row-end: 5;    
                padding-top: 2vh;    

            }
            li {
                list-style-type: none;
                height: 4vh;
                display: flex;
                align-items: center;
                justify-content: center;

            }

          }
        `}
        </style>
    </Layout>
)
    ;

Episode.getInitialProps = async function (context) {
    const { id } = context.query;
    const res = await fetch(`http://api.tvmaze.com/episodes/${id}`);
    const data = await res.json();

    return {
        episode: data,
        summary: data.summary
    };
};

export default Episode;
