import Link from 'next/link';
import Layout from '../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import { useRouter } from 'next/router';


const Blog = props => {

    const router = useRouter();

    return (
        <Layout>
            <h1>{props.data.name}'s unofficial Blog</h1>
            <p>{props.data.summary.replace(/<[^>]*>?/gm, '')}</p>

            <ul>
                <li>
                    <Link href="/seasons">
                        <a>Episodes</a>
                    </Link>
                </li>
                <li>
                    <Link href="/cast">
                        <a>Cast</a>
                    </Link></li>
                <li>
                    <Link href="/crew">
                        <a>Crew</a>
                    </Link></li>
            </ul>
            <div>{props.data.image ? <img src={props.data.image.original} /> : <noimg />}</div>
        </Layout>
    )
};

Blog.getInitialProps = async function (context) {
    const { id } = context.query;

    const res = await fetch(`https://api.tvmaze.com/singlesearch/shows?q=${id}`);
    const data = await res.json();
    console.log(id);
    return {
        data
    };
};

export default Blog
