import Link from 'next/link';
import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Episodes = props => (
    <Layout>
        <h1>Seasons!</h1>
        <ul>
            {props.seasons.map(season => (
                <li key={season.id} >
                    <Link href="/seasons/[id]" as={`/seasons/${season.id}`}>
                        <a>Season {season.number}........{season.premiereDate} - {season.endDate}</a>
                    </Link>
                </li>
            ))}
        </ul>

    </Layout>
);

Episodes.getInitialProps = async function (context) {
    const { id } = context.query
    const res = await fetch(`https://api.tvmaze.com/shows/${id}/seasons`);
    const data = await res.json();

    return {
        seasons: data.map(entry => entry)
    };
};

export default Episodes
