import Link from 'next/link';
import Layout from '../../components/MyLayout';

const Cast = props => (

    <Layout>
        <div className="container">
            <h1 className="title">{props.show.name}'s Unofficial Blog</h1>
            <ul className="castList">
                <h2>Cast</h2>
                {props.cast.map(person => (
                    <li key={person.person.id} >
                        <Link href="/cast/person/[id]" as={`/cast/person/${person.person.id}`}>
                            <a>{person.person.name}........{person.character.name}</a>
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
        <style>{`

            .summary {
                grid-column-start: 1;
                grid-column-end: 3;
                grid-row-start: 2;
                grid-row-end: 3;
                padding: 10px 15px 10px 20px;
            }
            .lista {

            }
            .list {
                grid-column-start: 1;
                grid-column-end: 3;
                grid-row-start: 3;
                grid-row-end: 5;    

            }
            .img {
                grid-column-start: 3;
                grid-column-end: 5;
                grid-row-start: 2;
                grid-row-end: 5;
                justify-self: center;
                margin-top: 20px;
            }
            .img > img {
                    object-fit: cover;
                   max-height: 100%;         
            }
            .title {
                grid-column-start: 1;
                grid-column-end: 5;
                grid-row-start: 1;
                grid-row-end: 2;
                display: flex;
                align-items: center;
                justify-content: center;
                margin-bottom: 0px;
            }
            .container {
                    padding-top: 15px;
                    justify-content: stretch;   
                    align-content: stretch;     
                    display: grid;
                    grid-template-columns: 20vw 20vw 20vw 30vw ;
                    grid-template-rows: 10vh 45vh 20vh 19vh;
                    margin-left: 6%; 
                    margin-right: 6%;      
                 }
            ul {
                margin: 0;
                padding: 0;
            }
            li {
                list-style-type: none;
                height: 13vh;
                display: flex;
                align-items: center;
                justify-content: center;

            }
            .episodes {
                background-color: #F4F4F4;
            }
            .cast {
                background-color: white;
            }
            .crew {
                background-color: #F4F4F4;
            }
            @media (max-width: 800px){
                Layout {
                    font-size: 10px;
                }
                .container {
                    padding-top: 15px;
                    justify-content: stretch;   
                    align-content: stretch;     
                    display: grid;
                    grid-template-columns: 20vw 20vw 20vw 20vw ;
                    grid-template-rows: 8vh 42vh 20vh 19vh;
                    margin-left: 9%; 
                    margin-right: 9%;
                    font-size: 9px;     
                 }
                 .img {
                    grid-column-start: 1;
                    grid-column-end: 5;
                    grid-row-start: 2;
                    grid-row-end: 3;
                    justify-self: center;
                }
                .summary {
                    grid-column-start: 1;
                    grid-column-end: 5;
                    grid-row-start: 3;
                    grid-row-end: 4;
                }
                .list {
                    grid-column-start: 1;
                    grid-column-end: 5;
                    grid-row-start: 4;
                    grid-row-end: 5;            
                }
                li {
                    list-style-type: none;
                    height: 6vh;
                    display: flex;
                    align-items: center;
                    justify-content: center;

                }

              }
       `}
        </style>

    </Layout>
);

Cast.getInitialProps = async function (context) {
    const { id } = context.query;
    const [show, data] = await Promise.all([
        fetch(`https://api.tvmaze.com/shows/${id}`).then(r => r.json()),
        fetch(`https://api.tvmaze.com/shows/${id}/cast`).then(r => r.json()),
    ])
    return {
        show,
        cast: data.map(entry => entry)
    };
};

export default Cast;
