import Layout from '../../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Episode = props => (

    <Layout>
        <h1>Season {props.episode.season} - Episode {props.episode.number}</h1>
        <h2>{props.episode.name}</h2>
        <p>{props.data.summary}</p>
        <p>Airdate: {props.episode.airdate}</p>

        <div>{props.episode.image ? <img src={props.episode.image.original} /> : <noimg />}</div>

    </Layout>
)
    ;

Episode.getInitialProps = async function (context) {
    const { id } = context.query;
    const res = await fetch(`http://api.tvmaze.com/episodes/${id}`);
    const data = await res.json();


    return {
        episode: data,

    };
};

export default Episode;
