import Link from 'next/link';
import Layout from '../components/MyLayout';

const Blog = props => (
    <Layout>
        <h1>The Mad Men unofficial Blog</h1>
        <p>{props.summary}</p>

        <ul>
            <li>
                <Link href="/seasons">
                    <a>Episodes</a>
                </Link>
            </li>
            <li>
                <Link href="/cast">
                    <a>Cast</a>
                </Link></li>
            <li>
                <Link href="/crew">
                    <a>Crew</a>
                </Link></li>
        </ul>
    </Layout>
);

Blog.getInitialProps = async function () {
    const res = await fetch('http://api.tvmaze.com/shows/385');
    const data = await res.json();

    console.log(data.summary)

    return {
        summary: data.summary
    };
};

export default Blog
