import Link from 'next/link';
import Layout from '../components/MyLayout';

const Cast = props => (

    <Layout>
        <h1>Cast</h1>
        <ul>
            {props.crew.map(person => (
                <li key={person.person.id} >
                    <Link href="/crew/[id]" as={`/cast/${person.person.id}`}>
                        <a>{person.type}........{person.person.name}</a>
                    </Link>
                </li>
            ))}
        </ul>

    </Layout>
);

Cast.getInitialProps = async function () {
    const res = await fetch('http://api.tvmaze.com/shows/385/crew');
    const data = await res.json();

    return {
        crew: data.map(entry => entry)
    };
};

export default Cast;
