import { useRouter } from 'next/router';
import Layout from '../../components/MyLayout';

const Post = props => (
    <Layout>
        <h1>Season {props.season[0].season}</h1>
        <ul>
            {props.season.map(season => (
                <li key={season[0].id}>
                    <Link href="/seasons/[id]" as={`/seasons/${season[0].id}`}>
                        <a>Episode {season[0].number}........{season[0].name}</a>
                    </Link>
                </li>
            ))}
        </ul>
    </Layout>
);

Post.getInitialProps = async function (context) {
    const { id } = context.query;
    const res = await fetch(`https://api.tvmaze.com/seasons/${id}/episodes`);
    const data = await res.json();
    console.log(data[0].name)

    return {
        season: data.map(entry => entry)
    };
};

export default Post;
